/**
* @author: hao.ling
* @date: 2020/8/5 14:47
* @Description: 系统模块列表
*/
<template>
    <div id="system-module-list">
        <a-layout>
            <a-layout-header id="system-header">
                <a-form layout="inline">
                    <a-form-item label="系统">
                        <a-select v-model="system_name" style="width: 120px" @change="handleProvinceChange" show-search
                                  allowClear placeholder="请选择">
                            <a-select-option v-for="province in provinceData" :key="province">
                                {{ province }}
                            </a-select-option>
                        </a-select>
                    </a-form-item>
                    <a-form-item label="模块">
                        <a-select v-model="module_name" style="width: 120px" allowClear placeholder="请选择">
                            <a-select-option v-for="city in cities" :key="city">
                                {{ city }}
                            </a-select-option>
                        </a-select>
                    </a-form-item>
                    <a-tooltip>
                        <template slot="title">查询</template>
                        <my-icon type="icon-search" class="icon" style="color: blue;"/>
                    </a-tooltip>
                    <a-tooltip>
                        <template slot="title">重置</template>
                        <my-icon type="icon-zhongzhi" class="icon" style="color: red;"/>
                    </a-tooltip>
                    <a-tooltip>
                        <template slot="title">新增</template>
                        <my-icon type="icon-add1" class="icon" style="color: green;"/>
                    </a-tooltip>
                </a-form>
            </a-layout-header>
            <a-layout-content style="background-color: white;">
                <a-table :dataSource="data_list" bordered :columns="column" :pagination="false"
                         style="margin-left: 30px;margin-right: 30px">
                    <span slot="action" slot-scope="text,record">
                        <my-icon type="icon-edit" class="icon" @click="edit(record.id)" style="color: lawngreen"/>
                        <my-icon type="icon-chakan" class="icon" @click="view(record.id)" style="color: blueviolet"/>
                        <a-popconfirm title="是否确认删除" ok-text="确认" cancel-text="取消" @confirm="del(record.id)">
                            <my-icon type="icon-delete" class="icon" @click="del(record.id)" style="color: red"/>
                        </a-popconfirm>
                    </span>
                </a-table>
            </a-layout-content>
        </a-layout>
    </div>
</template>

<script>
    const provinceData = ['Zhejiang', 'Jiangsu'];
    const cityData = {
        Zhejiang: ['Hangzhou', 'Ningbo', 'Wenzhou'],
        Jiangsu: ['Nanjing', 'Suzhou', 'Zhenjiang'],
    };

    const column = [
        {title: "操作", key: "action", align: "center", width: 130, scopedSlots: {customRender: "action"}, fixed: "left"},
        {title: "项目", dataIndex: "systemName", key: "system", align: "center", width: 200},
        {title: "模块", dataIndex: "moduleName", key: "module", align: "center", width: 200},
        {title: "模块地址", dataIndex: "address", key: "address", width: 200},
        {title: "环境", dataIndex: "pre", key: "pre", align: "center", width: 100},
        {title: "环境地址", dataIndex: "preAddress", key: "preAddress", width: 200},
        {title: "数据库地址", dataIndex: "dbAddress", key: "dbAddress", width: 200},
        {title: "通用数据", dataIndex: "data", key: "data", width: 200}
    ]

    export default {
        name: "systemModuleList",
        data() {
            return {
                provinceData,
                cityData,
                cities: cityData[provinceData[0]],
                secondCity: cityData[provinceData[0]][0],
                system_name: "请选择",
                module_name: "请选择",
                data_list: [{
                    systemName: "首页",
                    moduleName: "登录",
                    address: "http://www.baidu.com",
                    pre: "测试",
                    preAddress: "test.com",
                    dbAddress: "localhost",
                    data: "username:111"
                }], column,
            }
        },
        methods: {
            handleProvinceChange(value) {
                this.cities = cityData[value];
                this.secondCity = cityData[value];
            },
            // 编辑
            edit(value) {
                console.log(value)
            },
            // 查看
            view(value) {
                console.log(value)
            },
            // 删除
            del(value) {
                console.log(value)
            }
        }
    }
</script>

<style scoped>
    #system-header {
        height: 50px;
        background-color: white;
    }

    .icon {
        cursor: pointer;
        margin-top: 10px;
        font-size: 20px;
        margin-right: 10px;
    }
</style>
<style>
    .ant-table-thead > tr > th, .ant-table-tbody > tr > td {
        overflow-wrap: break-word;
        padding: 8px 16px;
    }
</style>
